rosnode kill /camera/realsense2_camera
roslaunch realsense2_camera rs_camera.launch enable_accel:=true enable_gyro:=true unite_imu_method:=linear_interpolation filters:=pointcloud enable_infra:=true pointcloud_texture_stream:=RS2_STREAM_INFRARED enable_color:=false gyro_fps:=400 accel_fps:=400 & sleep 5
rosrun dynamic_reconfigure dynparam set /camera/l500_depth_sensor "{'error_polling_enabled': True, 'inter_cam_sync_mode': False, 'global_time_enabled': False, 'freefall_detection_enabled': True, 'invalidation_bypass': False, 'enable_max_usable_range': False, 'alternate_ir': False, 'enable_ir_reflectivity': False, 'visual_preset': 0, 'laser_power': 100, 'confidence_threshold': 3, 'frames_queue_size': 16, 'min_distance': 490, 'receiver_gain': 8, 'post_processing_sharpening': 1, 'pre_processing_sharpening': 0, 'noise_filtering': 0, 'digital_gain': 2, 'host_performance': 1}"
